import React, { useState } from "react";



function CreateArea() {
    var [item,updateItem] = useState({
        title : "",
        content : ""
    })
    function itemChange(event){
        var {value, name} = event.target;
        console.log(name);
    }
  return (
    <div>
      <form>
        <input name="title" placeholder="Title" value={item.title} onChange={itemChange} />
        <textarea name="content" placeholder="Take a note..." rows="3" value={item.content} onChange={itemChange} />
        <button>Add</button>
      </form>
    </div>
  );
}

export default CreateArea;
